 class ArOut extends ArComponent { public function json($data = array(), array $options = array()) { if (empty($options['showJson']) || $options['showJson'] == true) : if (empty($options['data'])) : $retArr = array( 'ret_code' => '1000', 'ret_msg' => '', ); if (is_array($data)) : if (!isset($data['ret_code']) || !isset($data['ret_msg'])) : $retArr['data'] = $data; $retArr['total_lines'] = Ar::c('validator.validator')->checkMutiArray($data) ? (string)count($data) : 1; $retArr = array_merge($retArr, $options); else : if (!empty($data['error_msg']) && empty($data['ret_code'])) : $retArr['ret_code'] = "1001"; endif; $retArr = array_merge($retArr, $data); endif; else : $retArr['ret_msg'] = $data; endif; else : $retArr = $data; endif; echo json_encode($retArr); else : return $data; endif; } public function deBug($msg = '', $tag = 'TRACE', $show = false) { static $deBugMsg = array(); if (!array_key_exists($tag, $deBugMsg)) : $deBugMsg[$tag] = ''; endif; if ($msg) : if (preg_match("#\[[A-Z_]+\]$#", $msg)) : $msg = "<b>" . $msg . "</b>"; else : $msg = "&nbsp;&nbsp;" . $msg; endif; $deBugMsg[$tag] .= $msg . "<br>"; endif; if ($show && !empty($deBugMsg[$tag])) : $showContentBox = array( 'header' => '<div style="width:98%;bottom:30px"><div style="border-top:1px #666 dashed;background:#f1f1f1;text-align:center;font-size:20px;margin:10px 0px 10px;">[DEBUG ' . $tag . ' INFO] </div>', 'showMsg' => '<div style="padding:5px;background:#f3f3f1;line-height:30px">' . $deBugMsg[$tag] . '</div>', 'footer' => '</div>', ); if (arCfg('DEBUG_SHOW_TRACE')) : $showContentBox['trance'] = '<div style="background:#f8f8f8">RUN TIME : ' . (microtime(1) - AR_START_TIME) . 's</div>'; endif; if (arCfg('DEBUG_LOG')) : arComp('list.log')->record($showContentBox, 'debug'); switch ($tag) { case 'EXCEPTION': Header("HTTP/1.1 404 Not Found"); arSeg(array('segKey' => 'Redirect/404')); break; case 'SERVER_ERROR': Header("HTTP/1.1 500 App Error"); arSeg(array('segKey' => 'Redirect/500')); break; default: break; } else : echo join($showContentBox, ''); endif; $deBugMsg[$tag] = ''; endif; } public function array2xml(array $array, $xml = false, $root = 'root') { if ($xml === false) : $xml = new SimpleXMLElement('<' . $root . '/>'); endif; foreach ($array as $key => $value) : if (is_array($value)) : $this->array2xml($value, $xml->addChild($key)); else : $xml->addChild($key, $value); endif; endforeach; return $xml->asXML(); } public function xml2array($xml, $trimCdata = false) { $reg = "/<(\w+)[^>]*>([\\x00-\\xFF]*)<\\/\\1>/"; if (preg_match_all($reg, $xml, $matches)) : $count = count($matches[0]); for ($i = 0; $i < $count; $i++) : $subxml = $matches[2][$i]; $key = $matches[1][$i]; if (preg_match($reg, $subxml)) : $arr[$key] = $this->xml2array($subxml, $trimCdata); else : if ($trimCdata) : $arr[$key] = str_replace(array('<![CDATA[', ']]>'), '', $subxml); else : $arr[$key] = $subxml; endif; endif; endfor; endif; return $arr; } } 
 class ArUpload extends ArComponent { public $dest = ''; public $errorMsg = null; protected $upField = ''; public function errorMsg() { return $this->errorMsg; } static public $extensionMap = array( 'img' => array( 'jpg', 'gif', 'png' ), ); public function upload($upField, $dest = '', $extension = 'all') { $this->errorMsg = null; $this->upField = $upField; if (!empty($_FILES[$this->upField]) && empty($_FILES['error']) && is_uploaded_file($_FILES[$this->upField]['tmp_name'])) : if ($extension == 'all' || $this->checkFileType($this->getFileExtensionName($_FILES[$this->upField]['name']), $extension)) : $dest = empty($dest) ? arCfg('PATH.UPLOAD') : $dest; if (!is_dir($dest)) : mkdir($dest, 0777, true); endif; $upFileName = $this->generateFileName(); $destFile = rtrim($dest, DS) . DS . $upFileName; if (move_uploaded_file($_FILES[$this->upField]['tmp_name'], $destFile)) : else : $this->errorMsg = ''; endif; endif; else : if (empty($_FILES)) : $this->errorMsg = 'empty $_FILES uploaded file maybe exceeds the upload_max_filesize(' . ini_get('upload_max_filesize') . ') directive in php.ini'; else : if (empty($_FILES[$this->upField])) : $this->errorMsg = "Filed '$upField' invalid"; else : $this->errorMsg = $this->errorcodeToMessage($_FILES[$this->upField]['error']); endif; endif; endif; if (!!$this->errorMsg) : return false; else : return $upFileName; endif; } private function errorcodeToMessage($code) { switch ($code) { case UPLOAD_ERR_INI_SIZE: $message = "The uploaded file exceeds the upload_max_filesize(" . ini_get('upload_max_filesize') . ") directive in php.ini"; break; case UPLOAD_ERR_FORM_SIZE: $message = "The uploaded file exceeds the MAX_FILE_SIZE directive that was specified in the HTML form"; break; case UPLOAD_ERR_PARTIAL: $message = "The uploaded file was only partially uploaded"; break; case UPLOAD_ERR_NO_FILE: $message = "No file was uploaded"; break; case UPLOAD_ERR_NO_TMP_DIR: $message = "Missing a temporary folder"; break; case UPLOAD_ERR_CANT_WRITE: $message = "Failed to write file to disk"; break; case UPLOAD_ERR_EXTENSION: $message = "File upload stopped by extension"; break; default: $message = "Unknown upload error"; break; } return $message; } protected function checkFileType($extension, $aExtension = 'img') { if (is_array($aExtension)) : if (!in_array($extension, $aExtension)) : $this->errorMsg =" " . implode(',', $aExtension) . " "; endif; else : if (array_key_exists($aExtension, self::$extensionMap)) : if (!in_array($extension, self::$extensionMap[$aExtension])) : $this->errorMsg = " " . implode(',', self::$extensionMap[$aExtension]). " "; endif; elseif ($extension != $aExtension) : $this->errorMsg ="{$aExtension}"; endif; endif; return !$this->errorMsg; } protected function generateFileName() { return md5(time() . rand()) . '.' . $this->getFileExtensionName($_FILES[$this->upField]['name']); } protected function getFileExtensionName($fileName) { return strtolower(substr($fileName, strrpos($fileName, '.') + 1)); } } 
 class ArWeixin extends ArComponent { protected $appId; protected $appSecret; protected $token; protected $rawDataArray; private static $events = array(); static public function init($config = array(), $class = __CLASS__) { $obj = parent::init($config, $class); if (empty($obj->config['APPID'])) : throw new ArException("wx config mission error : " . "'APPID' required !"); else : $obj->appId = $obj->config['APPID']; endif; if (empty($obj->config['APPSECRET'])) : throw new ArException("wx config mission error : " . "'APPSECRET' required !"); else : $obj->appSecret = $obj->config['APPSECRET']; endif; if (empty($obj->config['TOKEN'])) : throw new ArException("wx config mission error : " . "'TOKEN' required !"); else : $obj->token = $obj->config['TOKEN']; endif; arComp('rpc.api')->curlOptions = array( CURLOPT_SSL_VERIFYPEER => false, ); arComp('rpc.api')->method = 'post'; return $obj; } public function __get($name) { if (isset($this->rawDataArray[$name])) : return $this->rawDataArray[$name]; else : return null; endif; } public function upload($filePath, $type) { $postFile = array('media' => '@' . $filePath); $accessToken = $this->getAccessToken(); $result = arComp('rpc.api')->remoteCall( 'http://file.api.weixin.qq.com/cgi-bin/media/upload?access_token=' . $accessToken . '&type=' . $type, $postFile); $resultArray = $this->handlerRemoteData($result); return $resultArray; } public function send(array $news) { $accessToken = $this->getAccessToken(); $jsonNews = urldecode(json_encode(arComp('format.format')->urlencode($news))); $result = arComp('rpc.api')->remoteCall( 'https://api.weixin.qq.com/cgi-bin/message/mass/send?access_token=' . $accessToken, $jsonNews); $resultArray = $this->handlerRemoteData($result); return $resultArray; } public function uploadNews(array $news) { $articles = array(); if (arComp('validator.validator')->checkMutiArray($news)) : foreach ($news as $new) : if (count($news) == 7) : $article['thumb_media_id'] = $new[0]; $article['author'] = $new[1]; $article['title'] = $new[2]; $article['content_source_url'] = $new[3]; $article['content'] = $new[4]; $article['digest'] = $new[5]; $article['show_cover_pic'] = $new[6]; $articles['articles'][] = $article; else : throw new ArException(""); endif; endforeach; else : $new = $news; if (count($new) == 7) : $article['thumb_media_id'] = $new[0]; $article['author'] = $new[1]; $article['title'] = $new[2]; $article['content_source_url'] = $new[3]; $article['content'] = $new[4]; $article['digest'] = $new[5]; $article['show_cover_pic'] = $new[6]; $articles['articles'][] = $article; else : throw new ArException(""); endif; $articles['articles'][] = $article; endif; if (empty($articles)) : throw new ArException(""); endif; $accessToken = $this->getAccessToken(); $jsonArticles = urldecode(json_encode(arComp('format.format')->urlencode($articles))); $result = arComp('rpc.api')->remoteCall( 'https://api.weixin.qq.com/cgi-bin/media/uploadnews?access_token=' . $accessToken, $jsonArticles); $resultArray = $this->handlerRemoteData($result); return $resultArray; } public function getAccessToken() { if (!arComp('cache.file')->get('wx_token')) : $result = arComp('rpc.api')->remoteCall('https://api.weixin.qq.com/cgi-bin/token', array('grant_type' => 'client_credential', 'appid' => $this->appId, 'secret' => $this->appSecret)); $resultArray = $this->handlerRemoteData($result); arComp('cache.file')->set('wx_token', $resultArray['access_token'], '7200'); endif; return arComp('cache.file')->get('wx_token'); } public function getOpenIdList() { $accessToken = $this->getAccessToken(); $result = arComp('rpc.api')->remoteCall('https://api.weixin.qq.com/cgi-bin/user/get?' . 'access_token=' . $accessToken); $resultArray = $this->handlerRemoteData($result); return $resultArray; } public function getJsTicket() { if (!arComp('cache.file')->get('wx_jsticket')) : $accessToken = $this->getAccessToken(); $result = arComp('rpc.api')->remoteCall('https://api.weixin.qq.com/cgi-bin/ticket/getticket?type=jsapi&' . 'access_token=' . $accessToken); $resultArray = $this->handlerRemoteData($result); arComp('cache.file')->set('wx_jsticket', $resultArray['ticket'], $resultArray['expires_in']); endif; return arComp('cache.file')->get('wx_jsticket'); } public function createMenu() { if (empty($this->config['menu'])) : throw new ArException("wx config mission error : " . "'menu' required !"); endif; $jsonPostMenu = urldecode(json_encode(arComp('format.format')->urlencode($this->config['menu']))); $result = arComp('rpc.api')->remoteCall('https://api.weixin.qq.com/cgi-bin/menu/create?access_token=' . $this->getAccessToken(), $jsonPostMenu); $resultArray = $this->handlerRemoteData($result); return $resultArray; } public function handlerRemoteData($data = '') { if ($data = json_decode($data, true)) : if (!empty($data['errcode'])) : throw new ArException("wx request error : " . $data['errmsg'] . ', code : ' . $data['errcode']); else : return $data; endif; else : throw new ArException("wx data parse error , data : " . $data); endif; } private function checkSignature() { if (!empty($this->config['notCheckSign'])) : return true; endif; $signature = arGet('signature'); $timestamp = arGet('timestamp'); $nonce = arGet('nonce'); $token = $this->token; $tmpArr = array($token, $timestamp, $nonce); sort($tmpArr); $tmpStr = implode($tmpArr); $tmpStr = sha1($tmpStr); if ($tmpStr == $signature) : arComp('list.log')->record('sign check true'); return true; else : arComp('list.log')->record('sign check false'); return false; endif; } public function response($type = 'text', $data = array()) { $result = call_user_func_array(array($this, 'process' . ucfirst($type)), array($data)); arComp('list.log')->record($result); echo $result; } public function listen() { if ($this->checkSignature()) : $this->processWxServerRequest(); $eventName = strtolower($this->rawDataArray['Event']); arComp('list.log')->record(array('ename' => $eventName)); $this->emit($eventName, ''); endif; } protected function processText($data) {