 class ArController { protected $assign = array(); protected $layOutFile = 'NOT_INIT'; public function init() { } public function __call($name, $params) { $mName = empty($params[0]) ? arCfg('requestRoute.a_c') : $params[0]; if ($name == 'module') : return arModule($mName); elseif ($name == 'model') : $m = $mName . 'Model'; return ArModel::model($m); else : throw new ArException("class do not have a method $name"); endif; } public function assign(array $vals) { foreach ($vals as $key => $val) : if (is_array($val) && isset($this->assign[$key]) && is_array($this->assign[$key])) : $this->assign[$key] = array_merge($this->assign[$key], $val); else : $this->assign[$key] = $val; endif; endforeach; } public function show($ckey = '', $defaultReturn = '', $show = true) { $rt = array(); if (empty($ckey)) : $rt = $this->assign; else : if (strpos($ckey, '.') === false) : if (isset($this->assign[$ckey])) : $rt = $this->assign[$ckey]; endif; else : $cE = explode('.', $ckey); $rt = $this->assign; while ($k = array_shift($cE)) : if (empty($rt[$k])) : $rt = $defaultReturn; break; else : $rt = $rt[$k]; endif; endwhile; endif; endif; if ($show) : echo $rt; else : return $rt; endif; } protected function display($view = '', $fetch = false) { $headerFile = ''; $footerFile = ''; if ($this->layOutFile === 'NOT_INIT') : $headerFile = AR_APP_VIEW_PATH . 'Layout' . DS . 'header' . '.' . arCfg('TPL_SUFFIX'); $footerFile = AR_APP_VIEW_PATH . 'Layout' . DS . 'footer' . '.' . arCfg('TPL_SUFFIX'); elseif ($this->layOutFile) : $headerFile = $this->layOutFile . '_header' . '.' . arCfg('TPL_SUFFIX'); $footerFile = $this->layOutFile . '_footer' . '.' . arCfg('TPL_SUFFIX'); endif; if ($headerFile) : if (is_file($headerFile)) : $this->fetch($headerFile); else : if ($this->layOutFile !== 'NOT_INIT') : throw new ArException("not fount layout header file : " . $headerFile, '2000'); endif; endif; endif; $this->fetch($view, $fetch); if ($footerFile) : if (is_file($footerFile)) : $this->fetch($footerFile); els